INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('1', '1', '中国文化概述', 'Overview of Chinese Culture', '本章介绍中国文化的基本概念、发展历程及其在世界文化中的地位和影响。通过对中国文化特点的概述，帮助学生建立对中国文化的整体认识。', 'This chapter introduces the basic concepts of Chinese culture, its development, and its position and influence in world culture.', '../picture/banner.jpg', '../picture/ppt/1/', '1', '10');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('2', '2', '中国哲学与思想', 'Chinese Philosophy and Thought', '本章探讨中国传统哲学思想体系，包括儒家、道家、法家、墨家等各派学说的核心理念及其对中国社会的深远影响。', 'This chapter explores the traditional Chinese philosophical systems including Confucianism, Taoism, Legalism and Mohism.', '../picture/b1.jpg', '../picture/ppt/2/', '1', '20');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('3', '3', '中国文学艺术', 'Chinese Literature and Art', '本章介绍中国古典文学与现代文学的发展，包括诗歌、散文、小说、戏剧等文学形式，以及其艺术特色和文化内涵。', 'This chapter introduces the development of classical and modern Chinese literature and various literary forms.', '../picture/b2.jpg', '../picture/ppt/3/', '1', '30');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('4', '4', '中国传统艺术', 'Chinese Traditional Arts', '本章详细介绍中国传统艺术形式，包括书法、绘画、音乐、舞蹈、戏曲等，探讨其艺术特点、表现手法和美学观念。', 'This chapter details traditional Chinese art forms including calligraphy, painting, music, dance, and opera.', '../picture/b3.jpg', '../picture/ppt/4/', '1', '40');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('5', '5', '中国历史文化', 'Chinese Historical Culture', '本章梳理中国悠久历史长河中的重要文化现象与历史事件，分析其对中国文化形成和发展的重要作用。', 'This chapter examines important cultural phenomena and historical events throughout Chinese history.', '../picture/b4.jpg', '../picture/ppt/5/', '1', '50');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('6', '6', '中国传统节日', 'Chinese Traditional Festivals', '本章介绍中国传统节日的起源、习俗和文化内涵，包括春节、元宵、清明、端午、中秋等重要节日的庆祝方式和象征意义。', 'This chapter introduces the origins and customs of Chinese traditional festivals like Spring Festival and Mid-Autumn Festival.', '../picture/nao.jpg', '../picture/ppt/6/', '1', '60');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('7', '7', '中国饮食文化', 'Chinese Food Culture', '本章探讨中国饮食文化的地域特色、烹饪技艺、饮食礼仪及其背后的文化哲学，介绍中国八大菜系及代表性美食。', 'This chapter discusses the regional characteristics of Chinese food culture and the eight major cuisine systems.', '../picture/yuanlin.webp', '../picture/ppt/7/', '1', '70');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('8', '8', '中国茶文化', 'Chinese Tea Culture', '本章详细介绍中国茶文化的历史发展、茶叶种类、制作工艺、品茶礼仪以及茶道精神，探讨茶文化与中国哲学思想的关系。', 'This chapter details the historical development of Chinese tea culture and its relationship with Chinese philosophy.', '../picture/cha.jpg', '../picture/ppt/8/', '1', '80');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('9', '9', '中国传统医学', 'Chinese Traditional Medicine', '本章介绍中医学的基本理论、诊断方法、治疗手段以及养生保健理念，探讨中医学的哲学基础及其在现代社会的应用与发展。', 'This chapter introduces the basic theories and diagnostic methods of Traditional Chinese Medicine.', '../picture/yliny.jpg', '../picture/ppt/9/', '1', '90');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('10', '10', '中国与世界文化交流', 'Chinese Cultural Exchange with the World', '本章探讨中国文化与世界其他文化的交流互动历史，包括丝绸之路、海上丝路的文化传播作用，以及当代中国文化的国际传播与影响。', 'This chapter explores the history of interaction between Chinese culture and other world cultures.', '../picture/sishu.png', '../picture/ppt/10/', '1', '100');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('11', '11', '中国传统手工艺', 'Chinese Traditional Crafts', '本章介绍中国传统手工艺的种类、工艺特点和文化价值，包括陶瓷、漆器、织绣、剪纸、玉雕等工艺的历史沿革和艺术特色。', 'This chapter introduces the types and cultural value of Chinese traditional crafts including ceramics and paper-cutting.', '../picture/kz.png', '../picture/ppt/11/', '1', '110');
INSERT INTO `db_test`.`chapters` (`id`, `chapter_number`, `title_zh`, `title_en`, `description_zh`, `description_en`, `cover_image`, `ppt_file`, `is_published`, `order_index`) VALUES 
('12', '12', '中国民俗文化', 'Chinese Folk Culture', '本章探讨中国各地民间文化传统，包括民间信仰、风俗习惯、传说故事、民间艺术等，展现中国文化的多样性和地域特色。', 'This chapter discusses the folk cultural traditions of various regions in China including folk beliefs and customs.', '../picture/PRli.png', '../picture/ppt/12/', '1', '120'); 